import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';

import './index.css';
import axios from 'axios';

let inited = false;

const root = document.getElementById('root');

ReactDOM.render(<Options />, root);

function Options () {

  const [litems, setLItems] = useState([]);
  const [ritems, setRItems] = useState([]);

  useEffect(() => {

    if (inited) {
      return;
    }

    inited = true;
    init();
  });

  return (
    <div className='container'>
      <div className='left'>
        <select multiple id='left'>
          {litems.map((item) => {
            return (<option key={item} value={item} onDoubleClick={() => {
              moveRight();
            }}>{item}</option>);
          })}
        </select>
      </div>

      <div className='middle'>
        <button type='button' onClick={() => { moveRight(); }}>{'>>'}</button>
        <button type='button' onClick={() => { moveLeft(); }}>{'<<'}</button>
      </div>

      <div className='right'>
        <select multiple id='right'>
          {ritems.map((item) => {
            return (<option key={item} value={item} onDoubleClick={() => {
              moveLeft();
            }}>{item}</option>);
          })}
        </select>
      </div>
    </div>
  );

  function init () {

    axios.get('http://localhost:8081/logs')
      .then(function (response) {
        setLItems(response.data.map((item) => {
          return item.Level;
        }));
      }).catch(function (error) {
        console.log(error);
      });

  }

  function moveRight () {

    let item = document.getElementById('left').value;
    if (!item) {
      alert('select a item to move');
      return;
    }

    let items = litems.slice().filter((_item) => {
      return item !== _item;
    });

    setLItems(items);

    ritems.push(item);

    setRItems(ritems);
  }

  function moveLeft () {

    let item = document.getElementById('right').value;
    if (!item) {
      alert('select a item to move');
      return; 
    }

    let items = ritems.slice().filter((_item) => {
      return item !== _item;
    });

    setRItems(items);

    litems.push(item);

    setLItems(litems);
  }

}
